import { useState, useEffect } from "react";
import axios from "axios";
import { toast } from "sonner";
import { useAuth, API } from "../../App";
import { Send, Clock, Users, CheckCircle, MessageCircle, MoreHorizontal } from "lucide-react";

const TELEGRAM_BOT_URL = "https://t.me/titly_social_bot";

const RequestsPage = ({ onRefresh }) => {
  const { token } = useAuth();
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchRequests();
  }, [token]);

  const fetchRequests = async () => {
    try {
      const response = await axios.get(`${API}/requests`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRequests(response.data);
    } catch (error) {
      toast.error("Failed to fetch requests");
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    const now = new Date();
    const diff = now - date;
    const hours = Math.floor(diff / (1000 * 60 * 60));
    if (hours < 1) return 'Just now';
    if (hours < 24) return `${hours}h`;
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case "in_progress": return <Clock size={14} />;
      case "matched": return <Users size={14} />;
      case "completed": return <CheckCircle size={14} />;
      default: return <Clock size={14} />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case "in_progress": return { bg: '#fef3c7', text: '#92400e' };
      case "matched": return { bg: '#dcfce7', text: '#166534' };
      case "completed": return { bg: '#dbeafe', text: '#1e40af' };
      default: return { bg: '#f3f4f6', text: '#374151' };
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-20">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div>
      <div className="sticky top-0 bg-white/80 backdrop-blur-md z-10 px-4 py-3 border-b border-gray-100">
        <h1 className="text-xl font-bold">My Requests</h1>
      </div>

      <div className="px-4 py-4 border-b border-gray-100">
        <div className="flex items-center gap-4 p-4 rounded-2xl" style={{ background: '#fef2f2' }}>
          <div 
            className="w-12 h-12 rounded-full flex items-center justify-center flex-shrink-0"
            style={{ background: '#E50914' }}
          >
            <MessageCircle size={24} className="text-white" />
          </div>
          <div className="flex-1">
            <p className="font-bold text-[15px]">To create a request, message Taj</p>
            <p className="text-gray-500 text-sm">Tell her who you need and she'll find matches for you</p>
          </div>
          <a
            href={TELEGRAM_BOT_URL}
            target="_blank"
            rel="noopener noreferrer"
            className="px-5 py-2.5 rounded-full text-white font-bold text-sm whitespace-nowrap"
            style={{ background: '#E50914' }}
          >
            Message Taj
          </a>
        </div>
      </div>

      {requests.length === 0 ? (
        <div className="flex flex-col items-center justify-center py-20 px-4 text-center">
          <Send size={48} className="text-gray-300 mb-4" />
          <h2 className="text-2xl font-bold mb-2">No requests yet</h2>
          <p className="text-gray-500">Message Taj to create your first request</p>
        </div>
      ) : (
        <div className="divide-y divide-gray-100">
          {requests.map((request) => {
            const statusStyle = getStatusColor(request.status);
            return (
              <article key={request.id} className="px-4 py-4 hover:bg-gray-50 transition-colors">
                <div className="flex gap-3">
                  <div 
                    className="w-10 h-10 rounded-full flex items-center justify-center flex-shrink-0"
                    style={{ background: request.category === 'pro' ? '#0a0a0a' : '#E50914' }}
                  >
                    <Send size={18} className="text-white" />
                  </div>

                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2 mb-1">
                      <span className="font-bold text-[15px]">You</span>
                      <span className="text-gray-500 text-[15px]">Â·</span>
                      <span className="text-gray-500 text-[15px]">{formatDate(request.created_at)}</span>
                      <button className="ml-auto p-1.5 -mr-1.5 rounded-full hover:bg-gray-200 transition-colors">
                        <MoreHorizontal size={18} className="text-gray-500" />
                      </button>
                    </div>

                    <div className="flex items-center gap-2 mb-2">
                      <span 
                        className="inline-flex items-center gap-1 px-2.5 py-0.5 rounded-full text-xs font-semibold"
                        style={{ background: statusStyle.bg, color: statusStyle.text }}
                      >
                        {getStatusIcon(request.status)}
                        <span className="capitalize">{request.status.replace('_', ' ')}</span>
                      </span>
                      <span 
                        className="inline-block px-2.5 py-0.5 rounded-full text-xs font-semibold"
                        style={{ 
                          background: request.category === 'pro' ? '#0a0a0a' : '#E50914',
                          color: 'white'
                        }}
                      >
                        {request.category === 'pro' ? 'Professional' : 'Community'}
                      </span>
                    </div>

                    <h3 className="font-bold text-[15px] mb-1">{request.title}</h3>
                    <p className="text-[15px] text-gray-700 leading-relaxed">{request.description}</p>
                  </div>
                </div>
              </article>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default RequestsPage;
