import { useState, useEffect } from "react";
import axios from "axios";
import { toast } from "sonner";
import { useAuth, API } from "../../App";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "../ui/dialog";
import { Input } from "../ui/input";
import { Sparkles, Check, X, UserPlus, MoreHorizontal, Share2, Copy } from "lucide-react";

const OpportunitiesPage = ({ onRefresh }) => {
  const { token } = useAuth();
  const [opportunities, setOpportunities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [actionLoading, setActionLoading] = useState(null);
  const [showInviteModal, setShowInviteModal] = useState(false);
  const [inviteLink] = useState("https://titly.social/invite/abc123");

  useEffect(() => {
    fetchOpportunities();
  }, [token]);

  const fetchOpportunities = async () => {
    try {
      const response = await axios.get(`${API}/opportunities`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOpportunities(response.data);
    } catch (error) {
      toast.error("Failed to fetch opportunities");
    } finally {
      setLoading(false);
    }
  };

  const handleAction = async (opportunityId, action) => {
    setActionLoading(opportunityId);
    try {
      await axios.post(
        `${API}/opportunities/${opportunityId}/action`,
        { action },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setOpportunities(opportunities.filter(o => o.id !== opportunityId));
      if (action === "approve") toast.success("Connection request sent!");
      else if (action === "decline") toast.success("Declined");
      onRefresh?.();
    } catch (error) {
      toast.error("Action failed");
    } finally {
      setActionLoading(null);
    }
  };

  const handleCopyLink = () => {
    navigator.clipboard.writeText(inviteLink);
    toast.success("Link copied!");
  };

  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    const now = new Date();
    const diff = now - date;
    const hours = Math.floor(diff / (1000 * 60 * 60));
    if (hours < 1) return 'Just now';
    if (hours < 24) return `${hours}h`;
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-20">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div>
      <div className="sticky top-0 bg-white/80 backdrop-blur-md z-10 px-4 py-3 border-b border-gray-100 flex items-center justify-between">
        <h1 className="text-xl font-bold">Opportunities</h1>
        <button
          onClick={() => setShowInviteModal(true)}
          className="flex items-center gap-2 px-4 py-2 rounded-full text-sm font-semibold hover:bg-gray-100 transition-colors"
        >
          <Share2 size={18} />
          Invite a friend
        </button>
      </div>

      {opportunities.length === 0 ? (
        <div className="flex flex-col items-center justify-center py-20 px-4 text-center">
          <Sparkles size={48} className="text-gray-300 mb-4" />
          <h2 className="text-2xl font-bold mb-2">No opportunities yet</h2>
          <p className="text-gray-500 mb-4">When someone's request matches your profile, it'll appear here.</p>
          <p className="text-sm text-gray-400">Start texting Taj to get more matches!</p>
        </div>
      ) : (
        <div className="divide-y divide-gray-100">
          {opportunities.map((opp) => (
            <article key={opp.id} className="px-4 py-4 hover:bg-gray-50 transition-colors">
              <div className="flex gap-3">
                <div 
                  className="w-10 h-10 rounded-full flex items-center justify-center text-white font-bold text-sm flex-shrink-0"
                  style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}
                >
                  {opp.from_user_name?.charAt(0).toUpperCase() || "?"}
                </div>

                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2 mb-1">
                    <span className="font-bold text-[15px] truncate">{opp.from_user_name}</span>
                    <span className="text-gray-500 text-[15px]">Â·</span>
                    <span className="text-gray-500 text-[15px]">{formatDate(opp.created_at)}</span>
                    <button className="ml-auto p-1.5 -mr-1.5 rounded-full hover:bg-gray-200 transition-colors">
                      <MoreHorizontal size={18} className="text-gray-500" />
                    </button>
                  </div>

                  <div className="mb-2">
                    <span 
                      className="inline-block px-2.5 py-0.5 rounded-full text-xs font-semibold"
                      style={{ 
                        background: opp.category === 'pro' ? '#0a0a0a' : '#E50914',
                        color: 'white'
                      }}
                    >
                      {opp.category === 'pro' ? 'Professional' : 'Community'}
                    </span>
                  </div>

                  <h3 className="font-bold text-[15px] mb-1">{opp.request_title}</h3>
                  <p className="text-[15px] text-gray-700 leading-relaxed mb-3">{opp.request_description}</p>

                  <div className="flex items-center gap-3">
                    <button
                      onClick={() => handleAction(opp.id, "approve")}
                      disabled={actionLoading === opp.id}
                      className="flex items-center gap-2 px-4 py-2 rounded-full text-sm font-semibold text-white transition-all hover:opacity-90"
                      style={{ background: '#22c55e' }}
                    >
                      <Check size={16} />
                      Approve
                    </button>
                    <button
                      onClick={() => handleAction(opp.id, "decline")}
                      disabled={actionLoading === opp.id}
                      className="flex items-center gap-2 px-4 py-2 rounded-full text-sm font-semibold border border-gray-300 hover:bg-gray-100 transition-colors"
                    >
                      <X size={16} />
                      Decline
                    </button>
                    <button
                      onClick={() => handleAction(opp.id, "refer")}
                      disabled={actionLoading === opp.id}
                      className="flex items-center gap-2 px-4 py-2 rounded-full text-sm font-semibold border border-gray-300 hover:bg-gray-100 transition-colors"
                    >
                      <UserPlus size={16} />
                      Refer
                    </button>
                  </div>
                </div>
              </div>
            </article>
          ))}
        </div>
      )}

      <Dialog open={showInviteModal} onOpenChange={setShowInviteModal}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Invite a friend</DialogTitle>
            <DialogDescription>
              Share this link with friends to invite them to titly
            </DialogDescription>
          </DialogHeader>

          <div className="py-4">
            <div className="flex items-center gap-2">
              <Input value={inviteLink} readOnly className="flex-1" />
              <button
                onClick={handleCopyLink}
                className="px-4 py-2.5 rounded-lg font-semibold text-white"
                style={{ background: '#E50914' }}
              >
                <Copy size={18} />
              </button>
            </div>

            <div className="mt-6 flex gap-3">
              <button 
                className="flex-1 py-3 rounded-full border border-gray-300 font-semibold hover:bg-gray-50 transition-colors"
                onClick={() => setShowInviteModal(false)}
              >
                Cancel
              </button>
              <button 
                className="flex-1 py-3 rounded-full text-white font-semibold"
                style={{ background: '#E50914' }}
                onClick={() => {
                  handleCopyLink();
                  setShowInviteModal(false);
                }}
              >
                Copy & Close
              </button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default OpportunitiesPage;
