import { useState, useEffect } from "react";
import axios from "axios";
import { toast } from "sonner";
import { useAuth, API } from "../../App";
import { Users, MoreHorizontal } from "lucide-react";

const ConnectionsPage = ({ onRefresh }) => {
  const { token, user } = useAuth();
  const [connections, setConnections] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState("connected");
  const [actionLoading, setActionLoading] = useState(null);

  useEffect(() => {
    fetchConnections();
  }, [token]);

  const fetchConnections = async () => {
    try {
      const response = await axios.get(`${API}/connections`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setConnections(response.data);
    } catch (error) {
      toast.error("Failed to fetch connections");
    } finally {
      setLoading(false);
    }
  };

  const handleApprove = async (connectionId) => {
    setActionLoading(connectionId);
    try {
      const response = await axios.post(
        `${API}/connections/${connectionId}/approve`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setConnections(connections.map(c => c.id === connectionId ? response.data : c));
      toast.success("Connection approved!");
      onRefresh?.();
    } catch (error) {
      toast.error(error.response?.data?.detail || "Failed to approve");
    } finally {
      setActionLoading(null);
    }
  };

  const pendingConnections = connections.filter(c => c.status === "pending");
  const connectedConnections = connections.filter(c => c.status === "connected");
  const needsApproval = (connection) => connection.status === "pending" && connection.initiated_by !== user?.id;

  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-20">
        <div className="spinner"></div>
      </div>
    );
  }

  const displayedConnections = activeTab === "connected" ? connectedConnections : pendingConnections;

  return (
    <div>
      <div className="sticky top-0 bg-white z-10 border-b border-gray-100">
        <h1 className="text-xl font-bold px-4 py-3">Connections</h1>
        <div className="flex">
          <button
            onClick={() => setActiveTab("connected")}
            className={`flex-1 py-4 text-center text-[15px] font-semibold relative transition-colors ${
              activeTab === "connected" ? 'text-black' : 'text-gray-500 hover:bg-gray-50'
            }`}
          >
            Connected
            {activeTab === "connected" && (
              <div className="absolute bottom-0 left-1/2 -translate-x-1/2 w-16 h-1 rounded-full" style={{ background: '#E50914' }} />
            )}
          </button>
          <button
            onClick={() => setActiveTab("pending")}
            className={`flex-1 py-4 text-center text-[15px] font-semibold relative transition-colors ${
              activeTab === "pending" ? 'text-black' : 'text-gray-500 hover:bg-gray-50'
            }`}
          >
            Pending
            {pendingConnections.length > 0 && (
              <span 
                className="ml-1.5 px-1.5 py-0.5 text-xs font-bold text-white rounded-full"
                style={{ background: '#E50914' }}
              >
                {pendingConnections.length}
              </span>
            )}
            {activeTab === "pending" && (
              <div className="absolute bottom-0 left-1/2 -translate-x-1/2 w-16 h-1 rounded-full" style={{ background: '#E50914' }} />
            )}
          </button>
        </div>
      </div>

      {displayedConnections.length === 0 ? (
        <div className="flex flex-col items-center justify-center py-20 px-4 text-center">
          <Users size={48} className="text-gray-300 mb-4" />
          <h2 className="text-2xl font-bold mb-2">No {activeTab} connections</h2>
          <p className="text-gray-500">
            {activeTab === "connected" 
              ? "When both parties approve, connections appear here"
              : "Pending connections will appear here"
            }
          </p>
        </div>
      ) : (
        <div className="divide-y divide-gray-100">
          {displayedConnections.map((connection) => (
            <div key={connection.id} className="px-4 py-4 flex items-center gap-3 hover:bg-gray-50 transition-colors">
              <div 
                className="w-12 h-12 rounded-full flex items-center justify-center text-white font-bold flex-shrink-0"
                style={{ background: 'linear-gradient(135deg, #E50914 0%, #ff4757 100%)' }}
              >
                {connection.other_user_name?.charAt(0).toUpperCase() || "?"}
              </div>
              <div className="flex-1 min-w-0">
                <div className="font-bold text-[15px] truncate">{connection.other_user_name}</div>
                <div className="text-gray-500 text-sm">
                  {connection.status === "connected" 
                    ? `Connected ${formatDate(connection.created_at)}`
                    : needsApproval(connection) 
                      ? "Wants to connect"
                      : "Waiting for approval"
                  }
                </div>
              </div>
              {needsApproval(connection) ? (
                <button
                  onClick={() => handleApprove(connection.id)}
                  disabled={actionLoading === connection.id}
                  className="px-4 py-2 rounded-full text-white text-sm font-bold"
                  style={{ background: '#0a0a0a' }}
                >
                  {actionLoading === connection.id ? '...' : 'Confirm'}
                </button>
              ) : connection.status === "connected" ? (
                <button className="px-4 py-2 rounded-full border border-gray-300 text-sm font-bold hover:bg-gray-100">
                  Message
                </button>
              ) : (
                <span className="text-gray-400 text-sm">Pending</span>
              )}
              <button className="p-2 rounded-full hover:bg-gray-200 transition-colors">
                <MoreHorizontal size={18} className="text-gray-500" />
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ConnectionsPage;
